mapper.select:
select

from so_write_off


    id = #{id}


    and so_id = #{soId}


    and shop_id = #{shopId}


    and shop_name = #{shopName}


    and user_id = #{userId}


    and sku_id = #{skuId}


    and coupon_id = #{couponId}


    and coupon_code = #{couponCode}


    and (create_time BETWEEN #{begainTime} and #{endTime})


    and is_bill = #{isBill,jdbcType=INTEGER}


order by create_time ASC
limit #{start}, #{rows}

select
SUM(paid_amount) as total_price,
SUM(coupon_amount)/10 as total_service
from so_write_off


    id = #{id}


    and so_id = #{soId}


    and shop_id = #{shopId}


    and shop_name = #{shopName}


    and user_id = #{userId}


    and sku_id = #{skuId}


    and coupon_id = #{couponId}


    and coupon_code = #{couponCode}


    and (create_time BETWEEN #{begainTime} and #{endTime})


    and is_bill = #{isBill,jdbcType=INTEGER}

select count(1)
from so_write_off


    id = #{id}


    and so_id = #{soId}


    and shop_id = #{shopId}


    and shop_name = #{shopName}


    and user_id = #{userId}


    and sku_id = #{skuId}


    and coupon_id = #{couponId}


    and coupon_code = #{couponCode}


    and (create_time BETWEEN #{begainTime} and #{endTime})


    and is_bill = #{isBill,jdbcType=INTEGER}

